// 获取当前 item 的数据
const item = $input.item.json;

// 确保 output 对象存在
if (!item.output || typeof item.output !== 'object') {
  return {
    json: {
      status: "error",
      source: "AddMeta",
      message: "Missing or invalid 'output' object"
    }
  };
}

// 在 output 内添加 meta 字段
item.output.meta = {
  field_explanation: {
    t: "time string, e.g., '2天前'",
    u: "user id",
    c: "message content",
    from_self: "boolean, whether sent by the bot itself",
    id: "user id",
    n: "nickname",
    o: "other nicknames/aliases",
    r: "role in group (e.g., admin, member, owner)",
    is_bot: "boolean"
  }
};

// 返回修改后的 item
return {
  json: item
};
