const formatted = $('Search Input').item.json;
const apiResult = $json.data[0].embedding;

const embeddingStr = `[${apiResult.join(',')}]`;

return {
  json: {
    ...formatted,
    embedding_str: embeddingStr
  }
};

// 获取当前 item 的数据
const item = $input.first().json;

// 确保 output 对象存在
if (!item.retrieved_context || typeof item.retrieved_context !== 'object') {
  return {
    json: {
      status: "error",
      source: "AddMeta",
      message: "Missing or invalid 'retrieved_context' object"
    }
  };
}

// 统计消息数量
const msgCount = Array.isArray(item.retrieved_context.messages)
  ? item.retrieved_context.messages.length
  : 0;

// 添加 meta 字段
item.retrieved_context.meta = {
  source: "HistorySearchTool", // 数据来源
  desc: "Top-N similar historical messages retrieved for LLM context",
  count: msgCount,
  field_explanation: {
    messages: {
      t: "time label, e.g., '2天前'",
      u: "user id",
      n: "nickname",
      c: "message content",
      sim: "similarity score (0~1)",
      from_self: "boolean, whether sent by the bot itself"
    },
    participants: {
      id: "user id",
      n: "nickname",
      o: "other nicknames/aliases",
      r: "role in group (admin/member/owner)",
      is_bot: "boolean"
    }
  }
};

// 返回修改后的 item
return {
  json: item
};

const now = new Date();
const items = $input.all().reverse(); // 按时间正序

function diffLabel(ms) {
  const min = Math.floor(ms / 60000);
  if (min <= 1) return "刚刚";
  if (min <= 5) return "5分钟前";
  if (min <= 10) return "10分钟前";
  if (min <= 30) return "30分钟前";
  const hours = Math.floor(min / 60);
  if (hours < 24) return `${hours}小时前`;
  return `${Math.floor(hours / 24)}天前`;
}

function collectNames(card, special, nick) {
  const arr = [];
  if (card && card.trim()) arr.push(card.trim());
  if (special && special.trim()) arr.push(special.trim());
  if (nick && nick.trim()) arr.push(nick.trim());
  return arr;
}

// participants 映射表 { user_inner_id: { id, n, o, r, is_bot } }
const participantsMap = {};
const messages = [];

// 注册参与者
function registerParticipant(userId, mainName, otherNames, role, isBot) {
  if (!participantsMap[userId]) {
    participantsMap[userId] = {
      id: userId || "无",        // user_id
      n: mainName,              // primary_name
      o: otherNames,            // other_names
      r: role || "无",           // role
      is_bot: !!isBot            // 是否机器人
    };
  }
}

for (const m of items) {
  const sent = new Date(m.json.sent_at);
  const label = diffLabel(now - sent);

  const allNames = collectNames(m.json.card_name, m.json.user_special_title, m.json.user_nickname);
  const mainName = allNames[0] || "未知用户";
  const otherNames = allNames.slice(1);
  const userId = m.json.user_inner_id;

  // 注册发送人
  registerParticipant(
    userId,
    mainName,
    otherNames,
    m.json.user_group_role,
    m.json.user_is_bot
  );

  let content = m.json.processed_text || "[空消息]";

  if (m.json.msg_type === 'reply' && m.json.reply_text) {
    const replyAllNames = collectNames(m.json.reply_card_name, m.json.reply_special_title, m.json.reply_nickname);
    const replyMain = replyAllNames[0] || "未知用户";
    const replyOthers = replyAllNames.slice(1);
    const replyUserId = m.json.reply_user_inner_id;

    // 注册被回复人
    registerParticipant(
      replyUserId,
      replyMain,
      replyOthers,
      m.json.reply_group_role,
      m.json.reply_is_bot
    );

    content = `回复 ${replyUserId}: ${m.json.reply_text}\n${content}`;
  } else if (m.json.msg_type === 'forward') {
    content = `转发消息:\n${content}`;
  }

  messages.push({
    u: userId,
    n: mainName,
    c: content,
    sim: Number(m.json.cosine_similarity?.toFixed(3) || 0), // 相似度缩写
    t: label,
    from_self: !!m.json.user_is_bot
  });
}

// participants 转为数组
const participants = Object.values(participantsMap);

return [{
  retrieved_context: {
    participants, // [{id, n, o, r, is_bot}]
    messages      // [{u, n, c, sim, t, from_self}]
  }
}];

